<?phpnamespace Zion\Acesso;use Zion\Exception\AcessoException;abstract class AcessoVO{    private $usuarioCod;    private $moduloNome;    private $acaoModuloIdPermissao;    /**     *      * @param int $valor     * @return \Zion\Acesso\AcessoVO     */    public function setUsuarioCod($valor)    {        if (!\is_numeric($valor)) {            throw new AcessoException('Código do usuário inválido!');        }        $this->usuarioCod = $valor;        return $this;    }    public function getUsuarioCod()    {        return $this->usuarioCod;    }    /**     *      * @param string $valor     * @return \Zion\Acesso\AcessoVO     */    public function setModuloNome($valor)    {        if (!\is_string($valor)) {            throw new AcessoException('Nome do módulo é inválido!');        }        $this->moduloNome = $valor;        return $this;    }    public function getModuloNome()    {        return $this->moduloNome;    }    /**     *      * @param string $valor     * @return \Zion\Acesso\AcessoVO     */    public function setAcaoModuloIdPermissao($valor)    {        if (!\is_string($valor)) {            throw new AcessoException('Ação do módulo é inválida!');        }        $this->acaoModuloIdPermissao = $valor;        return $this;    }    public function getAcaoModuloIdPermissao()    {        return $this->acaoModuloIdPermissao;    }}