<?php/** * \Zion\Layout\Html() *  * @author The Sappiens Team * @copyright 2014 * @version 2014 * @access public */ namespace Zion\Layout;class Html{    /**     * Html::abreTagAberta()     *      * @param mixed $tag     * @param mixed $atributos     * @param mixed $complementos     * @return     */    public function abreTagAberta($tag, array $atributos = array(), array $complementos = array())    {        $attr = vsprintf(str_repeat('%s ', count($atributos)), $this->montaAtributos($atributos));        $comp = vsprintf(str_repeat('%s ', count($complementos)), $this->montaComplementos($complementos));        return '<' . $tag . ' ' . $attr . $comp . '>' . "\n";    }    /**     * Html::abreTagFechada()     *      * @param mixed $tag     * @param mixed $atributos     * @param mixed $complementos     * @return     */    public function abreTagFechada($tag, array $atributos = array(), array $complementos = array())    {        $attr = vsprintf(str_repeat('%s ', count($atributos)), $this->montaAtributos($atributos));        $comp = vsprintf(str_repeat('%s ', count($complementos)), $this->montaComplementos($complementos));        return '<' . $tag . ' ' . $attr . $comp . '></' .$tag . '>' . "\n";    }        /**     * Html::entreTags()     *      * @param mixed $tag     * @param mixed $texto     * @return     */    public function entreTags($tag, $texto)    {        return '<'.$tag.'>'.$texto.'</'.$tag.'>' . "\n";    }    /**     * Html::abreComentario()     *      * @return     */    public function abreComentario()    {        return '<!-- ';    }    /**     * Html::fechaComentario()     *      * @return     */    public function fechaComentario()    {        return ' -->' . "\n";    }    /**     * Html::montaAtributos()     *      * @param mixed $atributos     * @return     */    private function montaAtributos(array $atributos)    {        $ret = [];        foreach ($atributos as $attr => $valor) {            $ret[] = $attr . '=' . '"' . $valor . '"';        }        return $ret;    }    /**     * Html::montaComplementos()     *      * @param mixed $complementos     * @return     */    private function montaComplementos(array $complementos)    {        $ret = [];        foreach ($complementos as $attr => $valor) {            $ret[] = $valor;        }        return $ret;    }    /**     * Html::fechaTag()     *      * @param mixed $tag     * @return     */    public function fechaTag($tag)    {        return '</' . $tag . '>' . "\n";    }}